mapper.insert:
INSERT INTO update_execution (team_id, execution_time)
SELECT #{updateExecution.teamId}, #{updateExecution.executionTime}
FROM DUAL
WHERE NOT EXISTS
        (SELECT 1
         FROM update_execution
         WHERE team_id = #{updateExecution.teamId}
           AND status = 'PENDING');

mapper.update:
UPDATE update_execution
SET status         = #{updateExecution.status},
    error_message  = #{updateExecution.errorMessage},
    execution_time = #{updateExecution.executionTime},
    retries        = #{updateExecution.retries}
WHERE id = #{updateExecution.id}
